function tabs(t){const a=document.querySelectorAll(t+" [data-tab]"),c=document.querySelectorAll(t+" [data-tab-content]");a.forEach((function(t){t.addEventListener("click",(function(e){t.classList.contains("activeb")||(a.forEach((function(t){t.classList.remove("activeb")})),t.classList.add("activeb"),c.forEach((function(t){t.classList.remove("active")})),document.querySelector("#"+e.target.dataset.tab).classList.add("active"))}))}))}tabs(".tab1");